import React from 'react';

class Form extends React.Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      usuario: '',
      senha:'',
      divaPopFavorita:'Selecione a melhor Diva do POP',
      justificativa:'',

    }

  }

  handleChange(event) {
    const { name } = event.target;
    const value = event.target.type === 'checkbox'
    ? event.target.checked
    : event.target.value;

    this.setState({
      [name]: value
    })
  }

  render() {
    const {usuario, senha, divaPopFavorita, justificativa} = this.state
    return (
      <div>
        <h1>Exercicio sobre formulários em React</h1>
        <div className="form-content">
          <form>
            <label htmlFor="usuario">
              Usuário:
              <input
                type="text"
                name="usuario"
                id="usuario"
                onChange={ this.handleChange }
                value={ usuario }
              ></input>
            </label>
            <br />
            <label htmlFor="senha">
              Senha:
              <input
                type="password"
                name="senha"
                id="senha"
                onChange={ this.handleChange }
                value={ senha }
              ></input>
            </label>
            <br />
            <select
              name="divaPopFavorita"
              onChange={ this.handleChange }
              value={ divaPopFavorita }
            >
              <option>Selecione a melhor Diva do POP</option>
              <option value="Beyonce">Beyonce</option>
              <option value="Rihanna">Rihanna</option>
              <option value="Lady Gaga">Lady Gaga</option>
            </select>
            <br />
            <label htmlFor="justificativa">
              Justifique sua escolha:
              <textarea
                name="justificativa"
                id="justificativa"
                onChange={ this.handleChange }
                value={ justificativa }
              ></textarea>
            </label>
            <br />
            <label htmlFor="concordo">
              Li e aceito os termos 
              <input
                type="checkbox"
                name="concordo"
                id="concordo"
              />
            </label>

          </form>
        </div>
      </div>
    )
  }
}

export default Form;
